<template>
  <div class="w-full">
    <CardHeader class="sm:mb-6 mb-3 sm:mt-0 md:mt-12 mt-16 px-4 lg:px-8">
      <CardTitle class="text-xl md:text-2xl font-bold sm:text-start text-center"
        >Profile</CardTitle
      >
    </CardHeader>

    <div class="flex items-center justify-center px-4 sm:px-6 lg:px-8">
      <div class="w-full max-w-[1150px]">
        <div class="flex flex-col gap-8">
          <!-- Profile Info Card -->
          <Card class="w-full bg-white p-4 sm:p-6 lg:p-9">
            <!-- Profile Header -->
            <div
              class="flex flex-col items-center justify-center mb-6 sm:mb-8 lg:mb-10"
            >
              <img
                :src="store?.userDetails?.photo_url"
                :alt="`${store?.userDetails?.display_name} profile picture`"
                class="w-20 h-20 sm:w-24 sm:h-24 lg:w-32 lg:h-32 rounded-full object-cover"
              />
              <p
                class="text-lg sm:text-xl lg:text-2xl font-medium mt-3 text-center mb-2"
              >
                <strong
                  class="text-xl sm:text-2xl lg:text-[28px] text-[#2D2D2D]"
                >
                  {{ store?.userDetails?.display_name }}
                </strong>
              </p>
              <span
                class="text-[#71717A] text-sm sm:text-base lg:text-lg font-medium"
                >Member</span
              >
            </div>

            <!-- Info Cards Grid -->
            <div
              class="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4 sm:gap-6 lg:gap-8"
            >
              <!-- Email Card -->
              <Card class="bg-[#F4FDFF] p-4 w-full">
                <div class="flex items-center justify-between mb-3">
                  <h3 class="text-[#2D2D2D] text-sm sm:text-base font-medium">
                    Email
                  </h3>
                  <Mail class="h-[16px] w-[16px] text-[#3A93A7]"></Mail>
                </div>
                <p
                  class="text-[#3A93A7] font-medium text-sm sm:text-base lg:text-lg"
                >
                  {{ store?.userDetails?.email }}
                </p>
              </Card>

              <!-- Phone Card -->
              <Card class="bg-[#FBF6FF] p-4 w-full">
                <div class="flex items-center justify-between mb-3">
                  <h3 class="text-[#2D2D2D] text-sm sm:text-base font-medium">
                    Phone
                  </h3>
                  <Phone class="h-[16px] w-[16px] text-[#6D458D]"></Phone>
                </div>
                <p
                  class="text-[#6D458D] text-sm sm:text-base lg:text-lg font-medium text-center sm:text-left"
                >
                  {{ store?.userDetails?.phone_number }}
                </p>
              </Card>

              <!-- Joined Card -->
              <Card class="bg-[#FBFFF6] p-4 w-full">
                <div class="flex items-center justify-between mb-3">
                  <h3 class="text-[#2D2D2D] text-sm sm:text-base font-medium">
                    Joined
                  </h3>
                  <CalendarDays
                    class="h-[16px] w-[16px] text-[#8FB560]"
                  ></CalendarDays>
                </div>
                <p
                  class="text-[#8FB560] text-sm sm:text-base lg:text-lg font-medium text-center sm:text-left"
                >
                  Joined:
                  {{ convertToLocalDateTime(store?.userDetails?.created_at) }}
                </p>
              </Card>
            </div>
          </Card>

          <!-- Subscription Card -->
          <Card class="w-full bg-white p-4 sm:p-6 lg:p-8 sm:mb-0 mb-5">
            <div class="flex flex-col lg:flex-row justify-between gap-6">
              <div class="space-y-4">
                <CardTitle
                  class="text-xl sm:text-2xl lg:text-3xl capitalize font-bold text-[#3D3894]"
                >
                  {{ store?.userDetails?.subscription?.tier }}
                </CardTitle>

                <!-- Credit Information -->
                <div class="grid grid-cols-1 sm:grid-cols-3 gap-4 sm:gap-6">
                  <div class="text-[#5D5D5D]">
                    <p class="text-sm lg:text-[17px]">Total Credit</p>
                    <span class="text-lg lg:text-xl font-medium text-[#2D2D2D]">
                      {{ store?.userDetails?.subscription?.totalCredit }}
                    </span>
                  </div>
                  <div class="text-[#5D5D5D]">
                    <p class="text-sm lg:text-[17px]">Remaining Credit</p>
                    <span class="text-lg lg:text-xl font-medium text-[#2D2D2D]">
                      {{ store?.userDetails?.credit }}
                    </span>
                  </div>
                  <div class="text-[#5D5D5D]">
                    <p class="text-sm lg:text-[17px]">Invoices left</p>
                    <span class="text-lg lg:text-xl font-medium text-[#2D2D2D]">
                      {{ store?.userDetails?.invoicesLeft }}
                    </span>
                  </div>
                </div>
                <!-- Action Buttons -->
                <div class="flex justify-start">
                  <Button
                    v-if="store?.userDetails?.subscription?.tier === 'free'"
